mapper.select:
select count(*) from t_order


        address like concat("%", #{address}, "%")


        and amount <= #{moneyLimit1}


        and amount >= #{moneyLimit2}


        and receiver like concat("%", #{name}, "%")


        and goodsName like concat("%", #{goods}, "%")


        and id = #{id}


        and stateId = #{state}

select id, userId, specId as goodsDetailId, goodsName as goods, specName as spec, number as goodsNum, amount, stateId, nickname, receiver as name, address, telephone as phone from t_order
address like concat("%", #{address}, "%")
and amount <= #{moneyLimit1}
and amount >= #{moneyLimit2}
and receiver like concat("%", #{name}, "%")
and goodsName like concat("%", #{goods}, "%")
and id = #{id}
and stateId = #{state}
limit #{pagesize} offset ${(currentPage - 1) * pagesize}
